let inputs,infos,indicateur_faible,indicateur_moyen,indicateur_fort;function init(){inputs=document.getElementsByTagName("input"),infos=document.getElementsByTagName("p"),indicateur_faible=document.getElementById("faible"),indicateur_moyen=document.getElementById("moyen"),indicateur_fort=document.getElementById("fort"),inputs.user.addEventListener("input",verifierUtlisateur),inputs.mail.addEventListener("input",verifierMail),inputs.mdpInfo.addEventListener("input",verifierMdp);document.getElementById("subForm")}function verifierUtlisateur(){inputs.user.value.length<3?infos.userInfo.style.color="red":infos.userInfo.style.color="black"}function verifierMail(){/^[\w-\.]+([\w-]+\.)+[\w-]{2,}$/.test(inputs.mail.value)?infos.mailU.style.color="black":infos.mailU.style.color="red"}function verifierMdp(){/^((?=.*?[a-zA-Z])(?=.*?[0-9])(?=.*[#?!@$%^&*-])).{6,}$/.test(inputs.mdpInfo.value)?infos.mdpI.style.color="black":infos.mdpI.style.color="red";let e=/[#?!@$%^&*-]/.test(inputs.mdpInfo.value),t=/[\d]/.test(inputs.mdpInfo.value);inputs.mdpInfo.value.length>9&&e&&t?(indicateur_faible.style.opacity="100%",indicateur_moyen.style.opacity="100%",indicateur_fort.style.opacity="100%"):inputs.mdpInfo.value.length>6&&(e||t)?(indicateur_faible.style.opacity="100%",indicateur_moyen.style.opacity="100%",indicateur_fort.style.opacity="0%"):(indicateur_faible.style.opacity="100%",indicateur_moyen.style.opacity="0%",indicateur_fort.style.opacity="0%")}onload=init,loginForm.addEventListener("submit",(e=>{e.preventDefault();let t=document.getElementById("user"),i=document.getElementById("mdpInfo");""==t.value||""==i.value?alert("Ensure you input a value in both fields!"):(alert("This form has been successfully submitted!"),t.value="",i.value="")})),localStorage.setItem("infoUtilisateur",{user:user,mail:mail,mdpInfo:mdpInfo});